mapper.select:

select *
from(select g.id,filename, filepath,member_id,gone_time,title,g.content,( select locname from location where id=g.location_id) locname,
								( select name_desc from location where id = g.location_id)name_desc,
                               ( select name from member where id=g.member_id) name,
                               ( select couname from course where id=g.course_id) couname
            from gone g, gone_file f where g.id=f.gone_id and g.type= #{type} and f.ptype= #{ptype} order by g.readcnt desc)
where rownum <= #{num}


select *
from(select g.id,filename, filepath,g.member_id,gone_time,title,g.content,contentr,( select locname from location where id=g.location_id) locname,
								( select name_desc from location where id = g.location_id)name_desc,
                               ( select name from member where id=g.member_id) name,
                               ( select couname from course where id=g.course_id) couname
            from gone g, gone_file f where g.id=f.gone_id and g.member_id = #{id} and g.type= #{type} and f.ptype= #{ptype} order by g.readcnt desc)
where rownum <= #{num}

select b.*, name 
from gone b inner join member m on b.member_id = m.id 
where b.id = #{id}
